import React from 'react'
import { slider } from '../../data/Events'
import icon from '../../assets/images/event/icon.svg'

const EventsCart = ({ events }) => {
    // Güvenli bir şekilde events'i kontrol edin ve varsayılan olarak boş bir dizi kullanın
    const eventList = events || []; // Eğer events undefined ise boş bir dizi kullan
  
    return (
      <div>
        {eventList.length > 0 ? (
          eventList.map((event, index) => (
            <div key={index}>
              {/* Etkinlik verilerini burada render edin */}
              <h3>{event.title}</h3>
              <p>{event.description}</p>
            </div>
          ))
        ) : (
          <p>No events available.</p> // Veri olmadığında gösterilecek mesaj
        )}
      </div>
    );
  };
  
  export default EventsCart;
