import "./blog.css"
import Blogscomponent from '../blogscomponent'
import Tomas1 from '../../../assets/images/Tomas1.png'
import Tomas2 from '../../../assets/images/Tomas2.png'
import { useTranslation } from 'react-i18next';
import { useEffect, useState } from "react"
import { fetchPageData } from "../../../api/api"
import { slider } from "../../../data/Events"
import i18n from './../../../i18n';
import { useParams } from "react-router-dom";



const Blog = () => {

    const { t } = useTranslation();
    // const events = slider || [];
    const { id } = useParams(); 
    const [events, setEvents] = useState( slider );
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);


    const languageMap = {
        en: "2", // English
        az: "1", // Azerbaijani
        ru: "3"  // Russian
      };    
  
    useEffect(() => {
  
      const getData = async () => {
        try {
            const currentLang = i18n.language;
            const languageId = languageMap[currentLang] || "2";
            const pageData = await fetchPageData(languageId);

            setEvents(pageData || []);
            console.log(pageData);
  
        } catch (error) {
          setError(error);
        } finally {
          setLoading(false);
        }
      };
  
      getData();
    }, [i18n.language]);
  
    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error.message}</div>;

    const targetEvent = events.find(
      (event) => event.id.toString() === id
  );


    return(
    <>        
         {targetEvent.postTranslations ? (
        <div className="blog">
          <div className="blog_head">
            <img className="blog_head_image" src={targetEvent.postTranslations.imageSrc} alt="MoreImg" />
            <div className="blog_head-texts">
              <h1 className="blog_head_title">{targetEvent.title}</h1>
              <p className="blog_head_subtitle">{targetEvent.content}</p>
            </div>
          </div>
        </div>
      ) : (
        <p>No blog post found.</p> 
      )}
            <div className='blogs'>
                <div className="container2">
                    <h3 className='blogs-head'>Blogs</h3>
                    <div className='home_components'>
                    <Blogscomponent
                    img ={Tomas1}
                    head ="Thomas Anders’s Baku concert"
                    text="Show your event to the world through any platform. We are leaders in streaming and..."
                    />
                    <Blogscomponent
                    img ={Tomas2}
                    head ="Thomas Anders’s Baku concert"
                    text="Show your event to the world through any platform. We are leaders in streaming and..."
                    /><Blogscomponent
                    img ={Tomas1}
                    head ="Thomas Anders’s Baku concert"
                    text="Show your event to the world through any platform. We are leaders in streaming and..."
                    /><Blogscomponent
                    img ={Tomas2}
                    head ="Thomas Anders’s Baku concert"
                    text="Show your event to the world through any platform. We are leaders in streaming and..."
                    /><Blogscomponent
                    img ={Tomas1}
                    head ="Thomas Anders’s Baku concert"
                    text="Show your event to the world through any platform. We are leaders in streaming and..."
                    /><Blogscomponent
                    img ={Tomas2}
                    head ="Thomas Anders’s Baku concert"
                    text="Show your event to the world through any platform. We are leaders in streaming and..."
                    />
                </div>
                </div>
            </div>
        </>
    )
}

export default Blog
