import './dropdown.css';
import React, { useState } from 'react';
import DropDownOpen from "../../assets/icons/X-icon.jpeg"
import DropDownClosed from "../../assets/icons/dropdown.svg"

const DropdownMenu = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const { t, i18n } = useTranslation();

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
  };


  return (
    <div className="dropdown-menu">
       <img
        onClick={toggleDropdown}
        className="dropdown-icon"
        src={isOpen ? DropDownOpen : DropDownClosed}
        alt="Toggle Dropdown"
      />
      {isOpen && (
<>              
    
    <div className="header_action">
            <select className="header_lang" onChange={(e) => changeLanguage(e.target.value)}>
              <option className="header_lang-option" value="en">EN</option>
              <option value="az">AZ</option>
            </select>
        </div>

        <ul className="dropdown-list">
          <li>Home</li>
          <li>Events</li>
          <li>Blog</li>
          <li>Sponsors</li>
          <li>About us</li>
        </ul>
        </>
      )}
    </div>
  );
};

export default DropdownMenu;
