import "./blog.css"
import Blogscomponent from '../blogscomponent'
import Tomas1 from '../../../assets/images/Tomas1.png'
import Tomas2 from '../../../assets/images/Tomas2.png'
import MoreImg from '../../../assets/icons/More Head.jpg'
import { useTranslation } from 'react-i18next';
import { useEffect, useState } from "react"
import { fetchPageData } from "../../../api/api"
import { slider } from "../../../data/Events"
import i18n from './../../../i18n';



const Blog = () => {

    const { t } = useTranslation();
    // const events = slider || [];
    
    const [events, setEvents] = useState( slider );
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);


    const languageMap = {
        en: "2", // English
        az: "1", // Azerbaijani
        ru: "3"  // Russian
      };    
  
    useEffect(() => {
  
      const getData = async () => {
        try {
            const currentLang = i18n.language;
            const languageId = languageMap[currentLang] || "2";
            const pageData = await fetchPageData(languageId);

            setEvents(pageData || []);
            console.log(pageData);
  
        } catch (error) {
          setError(error);
        } finally {
          setLoading(false);
        }
      };
  
      getData();
    }, [i18n.language]);
  
    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error.message}</div>;

    const targetEvent = events.find(
        (event) => event.id.toString() === languageMap[i18n.language]
      );


    return(
    <>        
        {targetEvent?.postTranslations.length > 0 ? (
            targetEvent.postTranslations.filter(item => item.id).map((item, index) => (
              <>
        <div className="blog">
            <div className="blog_head">
                <img className="blog_head_image" src={item.
imageSrc} alt="MoreImg" />
            <div className="blog_head-texts">
                <h1 className="blog_head_title">{item.title}</h1>
                <p className="blog_head_subtitle">{item.content}</p>
            </div>
            </div>
        </div>
            <div className='blogs'>
                <div className="container2">
                    <h3 className='blogs-head'>Blogs</h3>
                    <div className='home_components'>
                    <Blogscomponent
                    img ={Tomas1}
                    head ="Thomas Anders’s Baku concert"
                    text="Show your event to the world through any platform. We are leaders in streaming and..."
                    />
                    <Blogscomponent
                    img ={Tomas2}
                    head ="Thomas Anders’s Baku concert"
                    text="Show your event to the world through any platform. We are leaders in streaming and..."
                    /><Blogscomponent
                    img ={Tomas1}
                    head ="Thomas Anders’s Baku concert"
                    text="Show your event to the world through any platform. We are leaders in streaming and..."
                    /><Blogscomponent
                    img ={Tomas2}
                    head ="Thomas Anders’s Baku concert"
                    text="Show your event to the world through any platform. We are leaders in streaming and..."
                    /><Blogscomponent
                    img ={Tomas1}
                    head ="Thomas Anders’s Baku concert"
                    text="Show your event to the world through any platform. We are leaders in streaming and..."
                    /><Blogscomponent
                    img ={Tomas2}
                    head ="Thomas Anders’s Baku concert"
                    text="Show your event to the world through any platform. We are leaders in streaming and..."
                    />
                </div>
                </div>
            </div>
              </>
              ))
            ) : (
                <p>No events available.</p> 
            )}
        </>
    )
}

export default Blog
