import "./slider.css";
import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

import image1 from "../../assets/images/slider image.png";
import image2 from "../../assets/images/slider image.png";
import image3 from "../../assets/images/slider image.png";
import image4 from "../../assets/images/slider image.png";
import image5 from "../../assets/images/slider image.png";
import image6 from "../../assets/images/slider image.png";

const images = [image1, image2, image3, image4, image5, image6];

export default function HomeSlider() {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
    vertical: true,
    verticalSwiping: true,
    adaptiveHeight: true,
  };

  return (
    <div className="slider-container">
      <Slider {...settings}>
        {images.map((image, index) => (
          <div className="slider-container-images" key={index}>
            <img src={image} alt={`Slide ${index + 1}`} />
            <img src={image} alt={`Slide ${index + 1}`} />
            <img src={image} alt={`Slide ${index + 1}`} />
          </div>
        ))}
      </Slider>

    </div>
  );
}
