import "./blog.css"
import { useParams } from 'react-router-dom';
import { useEffect, useState } from "react";
import { fetchPageData } from "../../../api/api";
import { useTranslation } from 'react-i18next';
import Blogscomponent from "../blogscomponent";
import Tomas1 from '../../../assets/images/Tomas1.png'
import Tomas2 from '../../../assets/images/Tomas2.png'

const Blog = () => {
  const { id } = useParams(); // Get the blog post ID from the URL
  const { t } = useTranslation();
  const [post, setPost] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getData = async () => {
      try {
        const pageData = await fetchPageData(/* pass necessary params if needed */);
        const event = pageData.find(e => e.postTranslations.some(p => p.id === parseInt(id)));

        if (event) {
          const post = event.postTranslations.find(p => p.id === parseInt(id));
          setPost(post);
        }
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };

    getData();
  }, [id]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error.message}</div>;

  return (
    <>
      {post ? (
        <div className="blog">
          <div className="blog_head">
            <img className="blog_head_image" src={post.imageSrc} alt="MoreImg" />
            <div className="blog_head-texts">
              <h1 className="blog_head_title">{post.title}</h1>
              <p className="blog_head_subtitle">{post.content}</p>
            </div>
          </div>
        </div>
      ) : (
        <p>No blog post found.</p>
      )}
            <div className='blogs'>
                <div className="container2">
                    <h3 className='blogs-head'>Blogs</h3>
                    <div className='home_components'>
                    <Blogscomponent
                    img ={Tomas1}
                    head ="Thomas Andersâ€™s Baku concert"
                    text="Show your event to the world through any platform. We are leaders in streaming and..."
                    />
                    <Blogscomponent
                    img ={Tomas2}
                    head ="Thomas Andersâ€™s Baku concert"
                    text="Show your event to the world through any platform. We are leaders in streaming and..."
                    /><Blogscomponent
                    img ={Tomas1}
                    head ="Thomas Andersâ€™s Baku concert"
                    text="Show your event to the world through any platform. We are leaders in streaming and..."
                    /><Blogscomponent
                    img ={Tomas2}
                    head ="Thomas Andersâ€™s Baku concert"
                    text="Show your event to the world through any platform. We are leaders in streaming and..."
                    /><Blogscomponent
                    img ={Tomas1}
                    head ="Thomas Andersâ€™s Baku concert"
                    text="Show your event to the world through any platform. We are leaders in streaming and..."
                    /><Blogscomponent
                    img ={Tomas2}
                    head ="Thomas Andersâ€™s Baku concert"
                    text="Show your event to the world through any platform. We are leaders in streaming and..."
                    />
                </div>
                </div>
            </div>
        </>
    )
}

export default Blog
