import { useEffect, useState } from 'react';
import { fetchPageData } from '../../api/api';
import i18n from '../../i18n';
import './blogcomponents.css'
import { Link } from 'react-router-dom';

function blogscomponent(props) {

  const { t } = useTranslation();
  // const events = slider || [];
  
  const [events, setEvents] = useState( slider );
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  


  const languageMap = {
      en: "2", // English
      az: "1", // Azerbaijani
      ru: "3"  // Russian
    };    

  useEffect(() => {

    const getData = async () => {
      try {
          const currentLang = i18n.language;
          const languageId = languageMap[currentLang] || "2";
          const pageData = await fetchPageData(languageId);

          setEvents(pageData || []);
          console.log(pageData);

      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };

    getData();
  }, [i18n.language]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error.message}</div>;

  const targetEvent = events.find(
      (event) => event.id.toString() === languageMap[i18n.language]
    );


  return (
    <>

  {targetEvent?.postTranslations.length > 0 ? (
    targetEvent.postTranslations.filter(item => item.id).map((item, index) => (
    <div className='blogscomponent'>
      <div className="blogimg">
        <img src={props.img} />
        <button>
            <Link className='blogimg_link' to="/blog">Read More</Link>
        </button>
        <h4 className='head'>{props.head}</h4>
      </div>
      <div className="blogtext">
        <p className='body-text'>{props.text}</p>
      </div>
    </div>
          ))
        ) : (
            <p>No events available.</p> 
        )}
    </>
    

    
  )
}

export default blogscomponent
